#!/usr/bin/env python

long_number = ''''''

# The 'solution' variable should hold the
# solution when the script is done.
solution = 0
length   = 12

prod = 1
prodlist = []

l_iln = list(map(int, long_number)) #changes the string to individual integers in a list called l_iln
int1 = l_iln[0:11] #create list to hold 12 integers from list l_iln

for i in range(0,989): #989=1000-12+1
    i1 = l_iln[i:i+12] #creates another list of length 12
    for pos in i1:
        prod = pos * prod #updates the product
    prodlist.append(prod)
    prod = 1 #reset product to start with 1 when looping back to larger loop

solution = max(prodlist)
print(solution)


